mission.check:
package magic.magic;

import java.util.ArrayList;
import java.util.HashMap;
import java.util.Scanner;
import java.util.Map;

public class Digging {
    static Scanner scanner = new Scanner(System.in);
    static Map<String, Integer> elements = new HashMap<>();

    ArrayList<String> screenTheOre(String ore){
        String[] mass = ore.split(" ");
        ArrayList<String> screening = new ArrayList<>();
        for(var item: mass){
            if(elements.containsKey(item))
                screening.add(item);
        }
        return screening;
    }

    void main(){
        elements.put("Li", 0);
        elements.put("Cu", 0);
        elements.put("Si", 0);
        elements.put("Al", 0);
        elements.put("Fe", 0);
        var ore = scanner.nextLine();
        var mass = screenTheOre(ore);
        for(var item: mass)
            elements.put(item, elements.get(item)+1);
        for(var element: elements.keySet())
            System.out.printf("%s: %d\n", element, elements.get(element));
    }
}
